import React, {useState} from 'react'
import './domain.css'
import { useNavigate } from 'react-router-dom';

export default function Ecommerce() {

  const navigate = useNavigate();

  const [answers, setAnswers] = useState({
    qn1: [],
    qn2: [],
    qn3: [],
    r1: '',
    r2: '',
    qn4: [],
    r3: '',
    r4: '',
    r5: '',
    r6: '',
    r7: '',
    r8: '',
  });

  const handleInputChange = (event) => {
    const { name, value, type } = event.target;
    if (type === 'checkbox') {
      // Handle checkbox input
      const isChecked = event.target.checked;
      setAnswers((prevAnswers) => ({
        ...prevAnswers,
        [name]: isChecked
          ? [...prevAnswers[name], value]
          : prevAnswers[name].filter((item) => item !== value),
      }));
    } else {
      // Handle other input types
      setAnswers({ ...answers, [name]: value });
    }
  };

  

  const redirectToForm = () => {
    // Save the answers in localStorage
    localStorage.setItem('ecomAnswers', JSON.stringify(answers));
  
    // Redirect to the answers page
    navigate('/answers');
  };



  return (
    <div className="fm">
        <form>
          <center><h1 className="title">Ecommerce</h1></center>
            <div className="qns">
              <center><h1>Fill the following</h1></center><br/>
                <label><b>1. What is your primary goal ?</b></label><br/>
                <input type="checkbox" name="qn1" value="qn1" onChange={handleInputChange} /> Increase online sales and revenue <br />
                <input type="checkbox" name="qn1" value="qn1" onChange={handleInputChange} /> Enhance customer experience and engagement <br />
