import React, { useState } from 'react'
import './domain.css'

export default function Education() {
    const [formData, setFormData] = useState({
      qn1: [],
      qn2: [],
      r1: '',
      r2: '',
      r3: '',
      r4: '',
      r5: '',
      qn3: [],
      r6: '',
      r7: '',
      r8: '',
    });
  
    const handleCheckboxChange = (e) => {
      const { name, value, checked } = e.target;
      setFormData((prevData) => {
        const updatedData = { ...prevData };
        if (!updatedData[name]) {
          updatedData[name] = [];
        }
  
        if (checked) {
          updatedData[name].push(value);
        } else {
          updatedData[name] = updatedData[name].filter((item) => item !== value);
        }
  
        return updatedData;
      });
    };
  
    const handleRadioChange = (e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
    };
  
    const submitForm = async () => {
      try {
        const response = await fetch('/educ', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });
        if (response.status === 201) {
          window.location.href = '/answers';
        } else {
          console.error('Error submitting form');
        }
      } catch (error) {
        console.error('Error submitting form', error);
      }
    };

    app.get('/answers', async (_req, res) => {
      try {
        const answers = await EducationAnswer.find({}); // Retrieve answers from MongoDB (modify as needed)
        res.json(answers);
      } catch (error) {
