import React, { useState} from 'react'
import './domain.css'
import { useNavigate } from 'react-router-dom';

export default function Media() {
  const navigate = useNavigate();

  const [answers, setAnswers] = useState({
    qn1: [],
    qn2: [],
    r1: '',
    r2: '',
    r3: '',
    r4: '',
    r5: '',
    r6: '',
    r7: '',
    r8: '',
  });

  const handleInputChange = (event) => {
    const { name, value, type } = event.target;
    if (type === 'checkbox') {
      // Handle checkbox input
      const isChecked = event.target.checked;
      setAnswers((prevAnswers) => ({
        ...prevAnswers,
        [name]: isChecked
          ? [...prevAnswers[name], value]
          : prevAnswers[name].filter((item) => item !== value),
      }));
    } else {
      // Handle other input types
      setAnswers({ ...answers, [name]: value });
    }
  };

  

  const redirectToForm = () => {
    // Save the answers in localStorage
    localStorage.setItem('medAnswers', JSON.stringify(answers));
  
    // Redirect to the answers page
    navigate('/answers');
  };
  return (
    <div className="fm">
        <form>
            <center><h1 className="title">Media and Entertainment</h1></center>
            <div className="qns">
              <center><h1>Fill the following</h1></center>
                <label><b>1. What aspect of media and entertainment are you interested in?</b></label><br/>
                <input type="checkbox" name="qn1" value="qn1" onChange={handleInputChange}/> Content creation and production <br />
                <input type="checkbox" name="qn1" value="qn1" onChange={handleInputChange}/> Content distribution and streaming <br />
                <input type="checkbox" name="qn1" value="qn1" onChange={handleInputChange}/> Audience engagement and analytics <br />
                <input type="checkbox" name="qn1" value="qn1" onChange={handleInputChange}/> Others <br />
                <label><b>2. What tasks or activities do you want to accomplish using cloud services? </b></label><br/>
                <input type="checkbox" name="qn2" value="qn2" onChange={handleInputChange}/> Live Video Streaming and Broadcasting <br />
                <input type="checkbox" name="qn2" value="qn2" onChange={handleInputChange}/> Content Monetization and Subscription Services <br />
                <input type="checkbox" name="qn2" value="qn2" onChange={handleInputChange}/> News and Media Distribution <br />
                <input type="checkbox" name="qn2" value="qn2" onChange={handleInputChange}/> Digital Marketing and Advertising <br />
                <input type="checkbox" name="qn2" value="qn2" onChange={handleInputChange}/> Hosting Website <br />
                <label><b>3. Are you looking to enhance viewer engagement through interactive features like polls and Q&A?  </b></label><br/>
                <input type="text" name="r1" onChange={handleInputChange} /> <br />
                <label><b>4. Would you like to implement a media chatbot for viewer inquiries and support?</b></label><br/>
                <input type="text" name="r2"  onChange={handleInputChange}/> <br />
                <label><b>5. Do you require a cloud solution for live streaming and event broadcasting to a global audience?</b></label><br/>
                <input type="text" name="r3" onChange={handleInputChange}/> <br />
                <label><b>6. Do you need cloud-based tools for video editing and post-production tasks?  </b></label><br/>
                <input type="text" name="r4" onChange={handleInputChange} /> <br />
                <label><b>7. Do you require a cloud solution for content localization and multilingual subtitles for global audiences?</b></label><br/>
                <input type="text" name="r5" onChange={handleInputChange}/> <br />
                <label><b>8. Are you looking for a system to manage digital rights and content licensing for media distribution?</b></label><br/>
                <input type="text" name="r6" onChange={handleInputChange}/> <br />
                <label><b>9. Are there any specific geographic regions / requirements for your users?</b></label><br/>
                <textarea></textarea><br/>
                <label><b>10. Are you interested in managing and tracking viewer engagement and viewer feedback effectively?</b></label><br/>
                <input type="text" name="r7" onChange={handleInputChange}/> <br />
                <center><button className="btn1" onClick={redirectToForm}>Submit</button></center>
            </div>
        </form>
    </div>
  )
}
