import React, { useState } from 'react'
import './domain.css'
import { useNavigate } from 'react-router-dom';

export default function Education() {
  const navigate = useNavigate();

  const [answers, setAnswers] = useState({
    qn1: [],
    qn2: [],
    r1: '',
    r2: '',
    r3: '',
    r4: '',
    r5: '',
    qn3: [],
    r6: '',
    r7: '',
    r8: '',
  });

  const handleInputChange = (event) => {
    const { name, value, type } = event.target;
    if (type === 'checkbox') {
      // Handle checkbox input
      const isChecked = event.target.checked;
      setAnswers((prevAnswers) => ({
        ...prevAnswers,
        [name]: isChecked
          ? [...prevAnswers[name], value]
          : prevAnswers[name].filter((item) => item !== value),
      }));
    } else {
      // Handle other input types
      setAnswers({ ...answers, [name]: value });
    }
  };

  

  const redirectToForm = () => {
    // Save the answers in localStorage
    localStorage.setItem('educationAnswers', JSON.stringify(answers));
  
    // Redirect to the answers page
    navigate('/answers');
  };
  

  return (
    <div className="fm">
        <form>
            <center><h1 className="title">Education</h1></center>
            <div className="qns">
            <center><h1>Fill the following</h1></center>
                <label><b>1. What educational area are you interested in?</b></label><br/>
                <input type="checkbox" name="qn1" value="qn1" onChange={handleInputChange}  /> K-12 education <br />
                <input type="checkbox" name="qn1" value="qn1" onChange={handleInputChange}  /> Higher education <br />
                <input type="checkbox" name="qn1" value="qn1" onChange={handleInputChange}  /> Professional training <br />
                <input type="checkbox" name="qn1" value="qn1" onChange={handleInputChange}   /> Not sure <br />
                <label><b>2. What tasks or activities do you want to accomplish using cloud services? </b></label><br/>
                <input type="checkbox" name="qn2" value="qn2" onChange={handleInputChange}   /> Online learning platforms <br />
                <input type="checkbox" name="qn2" value="qn2" onChange={handleInputChange}   /> Student analytics and performance tracking <br />
                <input type="checkbox" name="qn2" value="qn2" onChange={handleInputChange}  /> Virtual Classrooms and Video Conferencing <br />
                <input type="checkbox" name="qn2" value="qn2" onChange={handleInputChange}  /> Hosting website <br />
                <input type="checkbox" name="qn2" value="qn2" onChange={handleInputChange}  /> Mobile learning apps <br />
                <label><b>3. Do you require a cloud solution for online testing and examination management?</b></label><br/>
                <input type="text" name="r1"  onChange={handleInputChange} /><br />
                <label><b>4. Would you like to implement a virtual library system for students to access digital resources and books?</b></label><br/>
                <input type="text" name="r2" onChange={handleInputChange}  /><br />
                <label><b>5. Do you require a cloud solution for managing educational research and data analysis projects?</b></label><br/>
                <input type="text" name="r3" onChange={handleInputChange} /><br />
                <label><b>6. Do you require cloud-based tools for remote student counseling and support services?</b></label><br/>
                <input type="text" name="r4" onChange={handleInputChange}  /><br />
                <label><b>7. Are you interested in a cloud service for interactive virtual labs and science experiments for students?</b></label><br/>
                <input type="text" name="r5" onChange={handleInputChange}  /><br />
                <label><b>8. What is the expected geographical distribution of your users?</b></label><br/>
                <input type="checkbox" name="qn3"   onChange={handleInputChange} /> Local / Regional <br />
                <input type="checkbox" name="qn3"  onChange={handleInputChange} /> National <br />
                <input type="checkbox" name="qn3"  onChange={handleInputChange}  /> Global <br />
                <label><b>9. Would you like to implement chatbot for managing queries?  </b></label><br/>
                <input type="text" name="r6" onChange={handleInputChange}  /> <br />
                <label><b>10. Do you require a cloud service for secure student identity verification and authentication? </b></label><br/>
                <input type="text" name="r7" onChange={handleInputChange}  /><br />
                <label><b>11. What is your expected user load? </b></label><br/>
                <input type="radio" name="r8"  onChange={handleInputChange}  /> A few hundred users (&gt;100) <br/> <input type="radio" name="r8" onChange={handleInputChange}  />Thousands of users (&gt; 1000) <br />
                <input type="radio" name="r8"  onChange={handleInputChange}  />Millions of users (&gt; 10000) <br/> <input type="radio" name="r8"  onChange={handleInputChange}   />Uncertain <br />
                <center><button className="btn1" onClick={redirectToForm}>Submit</button></center>
            </div>
        </form>
    </div>
  )
}
