import React from 'react';

export default function Answers(props) {
    const answers = props.location.state.answers;
  return (
    <div>
      <h1>Answers</h1>
      <p><b>Area of financial services:</b> {props.answers.area}</p>
      <p><b>Tasks or activities using cloud services:</b> {props.answers.tasks}</p>
      <p><b>Implement a financial chatbot:</b> {props.answers.chatbot}</p>
      <p><b>Manage and track insurance claims:</b> {props.answers.insurance}</p>
      <p><b>Expected geographical distribution of users:</b> {props.answers.geography}</p>
      <p><b>Existing software:</b> {props.answers.software}</p>
      <p><b>Expected user load:</b> {props.answers.userLoad}</p>
      <p><b>Handle payments and currency exchange:</b> {props.answers.payments}</p>
      <p><b>Implement secure document management:</b> {props.answers.documentManagement}</p>
      <p><b>Manage customer financial transactions and accounting:</b> {props.answers.transactions}</p>
    </div>
  );
}
