import React, { useEffect, useState } from 'react';

const answerDisplay = {
  r1: { r1: 'Yes', r2: 'No' },
  r2: { r1: 'Yes', r2: 'No' },
  r3: { r1: 'Yes', r2: 'No' },
  r4: { r1: 'Yes', r2: 'No' },
  r5: { r1: 'Yes', r2: 'No' },
  r6: { r6: 'Yes', r7: 'No' },
  r7: { r7: 'Yes', r8: 'No' },
};

export default function Answers() {
  const [storedAnswers, setStoredAnswers] = useState({
    qn1: [],
    qn2: [],
    r1: '',
    r2: '',
    r3: '',
    r4: '',
    r5: '',
    qn3: [],
    r6: '',
    r7: '',
    r8: '',
  });

  useEffect(() => {
    // Retrieve answers from localStorage when the component mounts
    const storedAnswers = localStorage.getItem('educationAnswers');
    if (storedAnswers) {
      setStoredAnswers(JSON.parse(storedAnswers));
    }
  }, []);

  return (
    <div>
      <h1>Answers</h1>
      {/* Display the answers here */}
      {storedAnswers ? (
        <div>
          <h2>1. What educational area are you interested in?</h2>
          <ul>
            {storedAnswers.qn1.map((answer, index) => (
              <li key={index}>{answer}</li>
            ))}
          </ul>

          <h2>2. What tasks or activities do you want to accomplish using cloud services?</h2>
          <ul>
            {storedAnswers.qn2.map((answer, index) => (
              <li key={index}>{answer}</li>
            ))}
          </ul>

          <h2>3. Do you require a cloud solution for online testing and examination management?</h2>
          <p>{answerDisplay.r1[storedAnswers.r1]}</p>

          <h2>4. Would you like to implement a virtual library system for students to access digital resources and books?</h2>
          <p>{answerDisplay.r2[storedAnswers.r2]}</p>

          <h2>5. Do you require a cloud solution for managing educational research and data analysis projects?</h2>
          <p>{answerDisplay.r3[storedAnswers.r3]}</p>

          <h2>6. Do you require cloud-based tools for remote student counseling and support services?</h2>
          <p>{answerDisplay.r4[storedAnswers.r4]}</p>

          <h2>7. Are you interested in a cloud service for interactive virtual labs and science experiments for students?</h2>
          <p>{answerDisplay.r5[storedAnswers.r5]}</p>

          <h2>8. What is the expected geographical distribution of your users?</h2>
          <ul>
            {storedAnswers.qn3.map((answer, index) => (
              <li key={index}>{answer}</li>
            ))}
          </ul>

          <h2>9. Would you like to implement a chatbot for managing queries?</h2>
          <p>{answerDisplay.r6[storedAnswers.r6]}</p>

          <h2>10. Do you require a cloud service for secure student identity verification and authentication?</h2>
          <p>{answerDisplay.r7[storedAnswers.r7]}</p>

          <h2>11. What is your expected user load?</h2>
          <p>{answerDisplay.r8[storedAnswers.r8]}</p>
        </div>
      ) : (
        <p>No answers found in localStorage.</p>
      )}
    </div>
  );
}
